import React, { useState } from 'react';
import classes from './Faq.module.css';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import Footer from '../../Footer/Footer';
import FAQBlock from './FAQBlock/FAQBlock';
import questionIcon from '../../UI/icons/question-svgrepo-com.svg';
import shippingIcon from '../../UI/icons/shipping-fast-solid-svgrepo-com.svg';
import contactsIcon from '../../UI/icons/contact-us-svgrepo-com.svg';

const Faq = () => {
    const [openDetails, setOpenDetails] = useState({
        shipping: false,
        contacts: false,
        faq: false
    });

    const toggleDetails = (section) => {
        setOpenDetails(prevState => ({
            ...prevState,
            [section]: !prevState[section]
        }));
    }

    const faqBlocks = [
        {
            icon: questionIcon,
            title: "Частые вопросы по заказам",
            description: "Предзаказы и их сроки, гарантии, отсутствие товара на сайте и другие вопросы.",
            section: "faq"
        },
        {
            icon: shippingIcon,
            title: "Доставка и оплата",
            description: "Варианты и сроки доставки, сроки отправки, способы оплаты, доставка в другие страны и другие вопросы.",
            section: "shipping"
        },
        {
            icon: contactsIcon,
            title: "Наши контакты",
            description: "Ссылки на наши социальные сети, перечень товаров под заказ и многое другое.",
            section: "contacts"
        }
    ];

    const faqDetails = {
        shipping: `
            Самовывоз в городе Москва в районе метро Ботанический Сад 
            Курьерская доставка по Москве осуществляется сервисами:
            Яндекс Go и Достависта 
            Доставка по России и СНГ осуществляется почтовыми службами: 
            Сдэк, Почта РФ и Boxberry  
            Стоимость доставки рассчитывается отдельно 
            Доставка по миру осуществляется курьерской службой EMS Почта России
            Стоимость доставки рассчитывается отдельно
        `,
        contacts: `
            ПОЗИЦИИ ПОД ЗАКАЗ: https://t.me/NiceDunksOrder
            Личный аккаунт в инстаграме: https://www.instagram.com/_goba_/
            Основная страница в вк: https://vk.com/nicedunks
            Мой профиль на авито: https://www.avito.ru/user/c560662529d1530f9fab8dd500a9934e/profile?src=sharing
            Аккаунт для продаж в инстаграмме: https://instagram.com/donalddunks/
            Менеджер по предзаказам: https://t.me/NiceDunksManager
        `
    };

    return (
        <div>
            <HeaderNavigation />
            <div className={classes.faq__wrapper}>
                {faqBlocks.map((block, index) => (
                    <React.Fragment key={index}>
                        <FAQBlock
                            icon={block.icon}
                            title={block.title}
                            description={block.description}
                            onClick={() => toggleDetails(block.section)}
                            isOpen={openDetails[block.section]}
                        />
                        {openDetails[block.section] && (
                            <div className={`${classes.details} ${openDetails[block.section] ? classes.visible : ''}`}>
                                {faqDetails[block.section].split('\n').map((line, index) => (
                                    <p key={index}>{line}</p>
                                ))}
                            </div>
                        )}
                    </React.Fragment>
                ))}
            </div>
            <Footer />
        </div>
    );
};

export default Faq;
