import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate, useLocation } from 'react-router-dom';

const ProductsPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [originalProducts, setOriginalProducts] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [sortOrder, setSortOrder] = useState('desc');
  const [sortBy, setSortBy] = useState('createdAt');
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [availableSizes, setAvailableSizes] = useState([]);
  const [selectedSizeOptions, setSelectedSizeOptions] = useState([]);
  const [allBrands, setAllBrands] = useState([]);
  const [selectedBrandOptions, setSelectedBrandOptions] = useState([]);
  const [clickCount, setClickCount] = useState(0);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `http://78.40.217.250:8081/api/sneakers?page=${page}&brands=${selectedBrandOptions.join(',')}&sizes=${selectedSizeOptions.join(',')}`
      );
      const data = await response.json();
      const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
      setOriginalProducts((prevOriginalProducts) => [...prevOriginalProducts, ...newProducts]);
      setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка при загрузке товаров', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [page, selectedBrandOptions, selectedSizeOptions]);

  useEffect(() => {
    const sizes = [...new Set(originalProducts.flatMap((product) => product.sizes))];
    sizes.sort((a, b) => parseFloat(a) - parseFloat(b));
    setAvailableSizes(sizes);

    const brands = [...new Set(originalProducts.map((product) => product.brand))];
    setAllBrands(brands);
  }, [originalProducts]);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const brandsParam = params.get('brands');
    const sizesParam = params.get('sizes');

    setSelectedBrandOptions(brandsParam ? brandsParam.split(',') : []);
    setSelectedSizeOptions(sizesParam ? sizesParam.split(',') : []);
  }, [location.search]);

  const handleBrandOptionClick = (brand) => {
    setSelectedBrandOptions((prevOptions) => [...new Set([...prevOptions, brand])]);
    setAllBrands((prevBrands) => [...new Set([...prevBrands, brand])]);
  };

  const handleRemoveSizeOption = (size) => {
    setSelectedSizeOptions((prevOptions) => prevOptions.filter((option) => option !== size));
  };

  const handleRemoveBrandOption = (brand) => {
    setSelectedBrandOptions((prevOptions) => prevOptions.filter((option) => option !== brand));
    setAllBrands((prevBrands) => prevBrands.filter((prevBrand) => prevBrand !== brand));
  };

  useEffect(() => {
    const params = new URLSearchParams();
    if (selectedBrandOptions.length > 0) {
      params.set('brands', selectedBrandOptions.join(','));
    }
    if (selectedSizeOptions.length > 0) {
      params.set('sizes', selectedSizeOptions.join(','));
    }
    navigate(`?${params.toString()}`, { replace: true });
  }, [selectedBrandOptions, selectedSizeOptions, navigate]);

  const applyFilters = () => {
    let filteredProducts = [...originalProducts];
    if (selectedSizeOptions.length > 0) {
      filteredProducts = filteredProducts.filter((product) =>
        product.sizes.some((size) => selectedSizeOptions.includes(size))
      );
    }
    if (selectedBrandOptions.length > 0) {
      filteredProducts = filteredProducts.filter((product) =>
        selectedBrandOptions.includes(product.brand)
      );
    }
    if (sortBy === 'price') {
      filteredProducts.sort((a, b) => {
        const priceA = a.price;
        const priceB = b.price;
        return sortOrder === 'asc' ? priceA - priceB : priceB - priceA;
      });
    }
    if (sortBy === 'createdAt') {
      filteredProducts.sort((a, b) => {
        const dateA = new Date(a.createdAt);
        const dateB = new Date(b.createdAt);
        return sortOrder === 'asc' ? dateA - dateB : dateB - dateA;
      });
    }

    setProducts(filteredProducts);
  };

  useEffect(() => {
    applyFilters();
  }, [selectedSizeOptions, selectedBrandOptions, sortOrder, sortBy, originalProducts]);

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li
              onClick={() => handleSizeFilterClick()}
              style={{ color: selectedSizeOptions.length > 0 ? '#AC8BD5' : '' }}
            >
              Размеры
              {showSizeOptions && (
                <div className={classes.SizeOptions}>
                  {availableSizes.map((size) => (
                    <button
                      key={size}
                      className={selectedSizeOptions.includes(size) ? classes.SelectedSize : ''}
                      onClick={() => handleSizeOptionClick(size)}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              )}
              {selectedSizeOptions.length > 0 && (
                <div className={classes.SelectedOptions}>
                  {selectedSizeOptions.map((size) => (
                    <button
                      key={size}
                      onClick={() => handleRemoveSizeOption(size)}
                      className={classes.SelectedOption}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              )}
            </li>
            <li>
              Бренды
              {allBrands.map((brand) => (
                <button
                  key={brand}
                  className={selectedBrandOptions.includes(brand) ? classes.SelectedBrand : ''}
                  onClick={() => handleBrandOptionClick(brand)}
                >
                  {brand}
                </button>
              ))}
              {selectedBrandOptions.length > 0 && (
                <div className={classes.SelectedOptions}>
                  {selectedBrandOptions.map((brand) => (
                    <button
                      key={brand}
                      onClick={() => handleRemoveBrandOption(brand)}
                      className={classes.SelectedOption}
                    >
                      {brand}
                    </button>
                  ))}
                </div>
              )}
            </li>
            <li
              onClick={() => handleSortClick('price')}
              style={{ color: sortBy === 'price' ? '#AC8BD5' : '' }}
            >
              Цена {sortBy === 'price' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
            <li
              onClick={() => handleSortClick('createdAt')}
              style={{ color: sortBy === 'createdAt' ? '#AC8BD5' : '' }}
            >
              Новизне {sortBy === 'createdAt' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
          </ul>
        </div>

        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>
                {formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}
              </h2>
              <h2>{product.price}₽</h2>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>

      {loading && <p>Загрузка...</p>}
    </div>
  );
};

export default ProductsPage;
