import React from 'react';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
  
  return (
    <div className={classes.productSizeDropdown}>
      <label htmlFor="sizeDropdown">Выберите размер:</label>
      <select id="sizeDropdown">
        {Array.from({ length: 22 }, (_, index) => index * 0.5 + 4).map(size => (
          <option key={size} value={size} disabled={!availableSizes || (availableSizes && !availableSizes.includes(size))}>
            {availableSizes && availableSizes.includes(size) ? size : `Под заказ (${size})`}
          </option>
        ))}
      </select>
    </div>
  );
};

export default MyDropdownMenu;
