import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [selectedSizeOptions, setSelectedSizeOptions] = useState([]);
  const navigate = useNavigate();

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
      const data = await response.json();
      const newProducts = data.filter(product => !loadedProductIds.has(product.vendorCode));
      setProducts((prevProducts) => [...prevProducts, ...newProducts]);
      setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map(product => product.vendorCode)]));
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка при загрузке товаров', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleScroll = () => {
    const scrollThreshold = 100;
    const isCloseToBottom = window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - scrollThreshold;

    if (isCloseToBottom && !loading) {
      fetchProducts();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading]);

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const formatBrand = (brand) => {
    const words = brand.split(/\s*,\s*/);
    if (words.length > 1) {
      return words.join(' X ');
    }
    return brand;
  };

  const handleSizeOptionClick = (size) => {
    const updatedSizes = toggleArrayItem(selectedSizeOptions, size);
    setSelectedSizeOptions(updatedSizes);
  };

  const toggleArrayItem = (array, item) => {
    if (array.includes(item)) {
      return array.filter((size) => size !== item);
    } else {
      return [...array, item];
    }
  };

  const renderSizeOptions = () => {
    const availableSizes = products.reduce((sizes, product) => {
      return [...sizes, ...product.sizes];
    }, []);
    const uniqueSizes = Array.from(new Set(availableSizes));
    const sortedSizes = uniqueSizes.sort((a, b) => parseFloat(a) - parseFloat(b));

    return sortedSizes.map((size) => (
      <button
        key={size}
        className={selectedSizeOptions.includes(size) ? classes.FilterOptionActive : classes.FilterOption}
        onClick={() => handleSizeOptionClick(size)}
      >
        {size}
      </button>
    ));
  };

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li>Размер</li>
            <li>Цена ↑</li>
            <li>Новизне ↓</li>
            <li>Бренд</li>
          </ul>
          <div className={classes.FilterSection}>
            <p>Размеры:</p>
            <div className={classes.SizeOptions}>{renderSizeOptions()}</div>
          </div>
        </div>
        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>{formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}</h2>
              <h2>{product.price}₽</h2>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>
      {loading && <p>Загрузка...</p>}
    </div>
  );
};

export default ProductsPage;
