import React, { useRef } from 'react';
import classes from './MyModalNavigation.module.css';

const MyModalNavigation = ({ visible, setVisible }) => {
  const modalRef = useRef(null);

  const handleClick = (event) => {
    if (modalRef.current && !modalRef.current.contains(event.target)) {
      setVisible(false);
    }
  };

  const rootClasses = [classes.myModalNavigation];

  if (visible) {
    rootClasses.push(classes.active);
  }

  return (
    <div className={rootClasses.join(' ')} onClick={handleClick}>
      <div ref={modalRef} className={classes.myModalNavigation__line}></div>
      <div className={classes.myModalNavigation__content}>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Vel nemo rerum, iure maxime ipsa voluptas soluta quas!
        Perspiciatis in necessitatibus, dicta dignissimos facilis temporibus quaerat! Porro dolorem voluptatem ratione
        dolor!
      </div>
    </div>
  );
};

export default My
