import React, { useState } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';
import './MyDropdownMenu.css';

const MyDropdownMenu = ({ sizes, category }) => {
  const [selectedSize, setSelectedSize] = useState(null);

  const renderOptionLabel = (option) => {
    const sizeValue = parseFloat(option.value);
    return sizeValue ? `${sizeValue} US` : 'ONESIZE';
  };

  const isSizeAvailable = (size) => {
    if (category.accessories) {
      // For accessories, check if ONESIZE is available
      return size === 'ONESIZE';
    } else if (category.clothes) {
      // For clothes, check if the size is in the available sizes
      return sizes.includes(size.toString());
    }
    return false;
  };

  const generateOptions = () => {
    if (category.accessories) {
      // For accessories, only include ONESIZE
      return [{ value: 'ONESIZE', label: renderOptionLabel({ value: 'ONESIZE' }) }];
    } else if (category.clothes) {
      // For clothes, include sizes from XS to XXL
      const options = [];
      for (let i = 0; i < 10; i++) {
        const sizeValue = (i * 0.5 + 4).toString();
        const isAvailable = isSizeAvailable(sizeValue);
        options.push({
          value: sizeValue,
          disabled: !isAvailable,
          label: renderOptionLabel({ value: sizeValue }),
        });
      }
      return options;
    }
    return [];
  };

  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  const options = generateOptions();

  return (
    <div className='DropDown'>
      <div className={classes.dropdownContainer}>
        <Dropdown
          id="sizeDropdown"
          options={options}
          values={selectedSize ? [{ value: selectedSize.toString() }] : []}
          onChange={handleChange}
          placeholder="Выберите размер"
          labelfield="label"
          valuefield="value"
          dropdownhandle={true}
          searchable={false}
          clearable={false}
          keepselectedinlist={false}
          closeonselect={true}
          contentrenderer={({ props, state }) => (
            <div {...props} style={{ ...props.style, zIndex: 9999 }}>
              {state.values.length > 0 ? renderOptionLabel(state.values[0]) : props.placeholder}
            </div>
          )}
          optionrenderer={({ props, state, methods }) => (
            <div
              {...props}
              onClick={() => !props.option.disabled && methods.addItem(props.option)}
              style={{ cursor: props.option.disabled ? 'not-allowed' : 'pointer', color: props.option.disabled ? '#999' : '#333' }}
            >
              {props.option.label}
            </div>
          )}
        />
        <button className={classes.addToCartButton}>Добавить в корзину</button>
      </div>
      <div className={classes.SizeNotAvailable}>
        <div className={classes.ContactUs}>
          {category.accessories && (
            <>
              Вашего размера не оказалось в наличии?<br /> Свяжитесь с нашим <a href="https://t.me/NiceDunksManager">менеджером</a> и он обязательно поможем вам в этом вопросе!
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default MyDropdownMenu;
