// ProductPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';

const ProductPage = () => {
  const { silhouette, color, vendorCode } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetch(`http://78.40.217.250:8081/api/sneakersById?id=${vendorCode.toUpperCase()}`)
      .then(response => response.json())
      .then(data => setProduct(data))
      .catch(error => console.error('Ошибка', error));
  }, );

  return (
    <div>
        <HeaderNavigation />
        <div className="product__wrapper"></div>
    </div>
  );
};

export default ProductPage;
