import React, { useState } from 'react';
import classes from './MainPage.module.css';
import MyBurger from '../../UI/MyBurger/MyBurger';
import cart from '../../UI/icons/cart.svg';
import MyModalNavigation from '../../UI/MyModalNavigation/MyModalNavigation';

const MainPage = () => {
  const [modal, setModal] = useState(false);

  return (
    <div>
      <div className={classes.banner}>
        <MyBurger setModal={setModal} /> {/* Передача функции setModal в компонент MyBurger */}
        <img src={cart} alt="cart" className={classes.cart} />
        <MyModalNavigation visible={modal} setVisible={setModal} /> {/* Передача состояния и функции setModal в компонент MyModalNavigation */}
      </div>
    </div>
  );
};

export default MainPage;
