import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate, useLocation } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [sortOrder, setSortOrder] = useState('desc');
  const [sortBy, setSortBy] = useState('createdAt');
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [availableSizes, setAvailableSizes] = useState([]);
  const [selectedSizeOptions, setSelectedSizeOptions] = useState([]);
  const [showBrandOptions, setShowBrandOptions] = useState(false);
  const [availableBrands, setAvailableBrands] = useState([]);
  const [selectedBrandOptions, setSelectedBrandOptions] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
        const data = await response.json();
        const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
        newProducts.reverse();
        setProducts((prevProducts) => [...prevProducts, ...newProducts]);
        setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
        setPage((prevPage) => prevPage + 1);
      } catch (error) {
        console.error('Ошибка при загрузке товаров', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [page]);

  const handleScroll = () => {
    const scrollThreshold = 100;
    const isCloseToBottom =
      window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - scrollThreshold;

    if (isCloseToBottom && !loading) {
      setPage((prevPage) => prevPage + 1);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading]);

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const handleSortClick = (sortType) => {
    if (sortType === sortBy) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(sortType);
      setSortOrder('asc');
    }
  };

  const handleSizeFilterClick = () => {
    setShowSizeOptions(!showSizeOptions);
  };

  const handleSizeOptionClick = (size) => {
    setSelectedSizeOptions((prevOptions) => {
      if (prevOptions.includes(size)) {
        return prevOptions.filter((option) => option !== size);
      } else {
        return [...prevOptions, size];
      }
    });
  };

  const handleBrandFilterClick = () => {
    setShowBrandOptions(!showBrandOptions);
  };

  const handleBrandOptionClick = (brand) => {
    setSelectedBrandOptions((prevOptions) => {
      if (prevOptions.includes(brand)) {
        return prevOptions.filter((option) => option !== brand);
      } else {
        return [...prevOptions, brand];
      }
    });
  };

  const applyFilters = () => {
    const params = new URLSearchParams(location.search);

    if (selectedSizeOptions.length > 0) {
      params.set('sizes', selectedSizeOptions.join(','));
    } else {
      params.delete('sizes');
    }

    if (selectedBrandOptions.length > 0) {
      params.set('brands', selectedBrandOptions.join(','));
    } else {
      params.delete('brands');
    }

    params.set('sortOrder', sortOrder);
    params.set('sortBy', sortBy);

    navigate(`?${params.toString()}`);
  };

  useEffect(() => {
    applyFilters();
  }, [selectedSizeOptions, selectedBrandOptions, sortOrder, sortBy]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://78.40.217.250:8081/api/sneakers${location.search}`);
        const data = await response.json();
        const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
        newProducts.reverse();
        setProducts((prevProducts) => [...prevProducts, ...newProducts]);
        setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
      } catch (error) {
        console.error('Ошибка при загрузке товаров', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [location.search]);

  useEffect(() => {
    const sizes = [...new Set(products.flatMap((product) => product.sizes))];
    sizes.sort((a, b) => parseFloat(a) - parseFloat(b));
    setAvailableSizes(sizes);

    const brands = [...new Set(products.map((product) => product.brand))];
    brands.sort();
    setAvailableBrands(brands);
  }, [products]);

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li
              onClick={() => handleSizeFilterClick()}
              style={{ color: selectedSizeOptions.length > 0 ? '#AC8BD5' : '' }}
            >
              Размеры
              {showSizeOptions && (
                <div className={classes.SizeOptions}>
                  {availableSizes.map((size) => (
                    <button
                      key={size}
                      className={selectedSizeOptions.includes(size) ? classes.SelectedSize : ''}
                      onClick={() => handleSizeOptionClick(size)}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              )}
            </li>
            <li
              onClick={() => handleSortClick('price')}
              style={{ color: sortBy === 'price' ? '#AC8BD5' : '' }}
            >
              Цена {sortBy === 'price' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
            <li
              onClick={() => handleSortClick('createdAt')}
              style={{ color: sortBy === 'createdAt' ? '#AC8BD5' : '' }}
            >
              Новизне {sortBy === 'createdAt' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
            <li
              onClick={() => handleBrandFilterClick()}
              style={{ color: selectedBrandOptions.length > 0 ? '#AC8BD5' : '' }}
            >
              Бренды
              {showBrandOptions && (
                <div className={classes.BrandOptions}>
                  {availableBrands.map((brand) => (
                    <button
                      key={brand}
                      className={selectedBrandOptions.includes(brand) ? classes.SelectedBrand : ''}
                      onClick={() => handleBrandOptionClick(brand)}
                    >
                      {brand}
                    </button>
                  ))}
                </div>
              )}
            </li>
          </ul>
        </div>

        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>
                {product.brand + ' ' + product.silhouette + ' ' + product.color}
              </h2>
              <h2>{product.price}₽</h2>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>

      {loading && <p>Загрузка...</p>}
    </div>
  );
};

export default ProductsPage;
