import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import classes from './ProductPage.module.css';

const ProductPage = () => {
  const { vendorCode } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetch(`http://78.40.217.250:8081/api/sneakersById?id=${vendorCode.toUpperCase()}`)
      .then(response => response.json())
      .then(data => setProduct(data))
      .catch(error => console.error('Ошибка', error));
  }, [vendorCode]);

  return (
    <div>
      <HeaderNavigation />
      <div className={classes.productPage__wrapper}>
        {product && product.images ? (
          <div className={classes.productPage__images}>
            {product.images.map((image, index) => (
              <img key={index} src={image} alt={`Product Image ${index}`} />
            ))}
            <img src="https://image.goat.com/transform/v1/attachments/product_template_additional_pictures/images/077/966/795/original/912046_01.jpg.jpeg?action=crop&amp;width=600" alt="Product Image 0" />
            <img src="https://image.goat.com/transform/v1/attachments/product_template_additional_pictures/images/077/966/795/original/912046_01.jpg.jpeg?action=crop&amp;width=600" alt="Product Image 0" />
          </div>
        ) : (
          <p>Loading...</p>
        )}
        .ProductPage__brand
      </div>
    </div>
  );
};

export default ProductPage;
