import React, { useState } from 'react';
import classes from './Faq.module.css';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import Footer from '../../Footer/Footer';
import FAQBlock from './FAQBlock/FAQBlock';
import questionIcon from '../../UI/icons/question-svgrepo-com.svg';
import shippingIcon from '../../UI/icons/shipping-fast-solid-svgrepo-com.svg';
import contactsIcon from '../../UI/icons/contact-us-svgrepo-com.svg';

const Faq = () => {
    const [shippingDetailsOpen, setShippingDetailsOpen] = useState(false);
    const [contactsDetailsOpen, setContactsDetailsOpen] = useState(false);
    const [faqDetailsOpen, setFaqDetailsOpen] = useState(false);
    
    const toggleShippingDetails = () => {
        setShippingDetailsOpen(!shippingDetailsOpen);
        setContactsDetailsOpen(false); 
        setFaqDetailsOpen(false); // Закрываем другие блоки при открытии этого
    }

    const toggleContactsDetails = () => {
        setContactsDetailsOpen(!contactsDetailsOpen);
        setShippingDetailsOpen(false); 
        setFaqDetailsOpen(false); // Закрываем другие блоки при открытии этого
    }

    const toggleFaqDetails = () => {
        setFaqDetailsOpen(!faqDetailsOpen);
        setShippingDetailsOpen(false); 
        setContactsDetailsOpen(false); // Закрываем другие блоки при открытии этого
    }

    return (
        <div>
            <HeaderNavigation />
            <div className={classes.faq__wrapper}>
                <FAQBlock
                    icon={questionIcon}
                    title="Частые вопросы по заказам"
                    description="Предзаказы и их сроки, гарантии, отсутствие товара на сайте и другие вопросы."
                    onClick={toggleFaqDetails}
                />
                <li className={`${classes.shippingDetails} ${faqDetailsOpen ? classes.visible : ''}`}>
                    {/* Ваш текст для блока "Частые вопросы по заказам" */}
                </li>
                <FAQBlock
                    icon={shippingIcon}
                    title="Доставка и оплата"
                    onClick={toggleShippingDetails}
                    description="Варианты и сроки доставки, сроки отправки, способы оплаты, доставка в другие страны и другие вопросы."
                />
                <li className={`${classes.shippingDetails} ${shippingDetailsOpen ? classes.visible : ''}`}>
                    {/* Ваш текст для блока "Доставка и оплата" */}
                </li>
                <FAQBlock
                    icon={contactsIcon}
                    title="Наши контакты"
                    onClick={toggleContactsDetails}
                    description="Ссылки на наши социальные сети, перечень товаров под заказ и многое другое."
                />
                <li className={`${classes.shippingDetails} ${contactsDetailsOpen ? classes.visible : ''}`}>
                    {/* Ваш текст для блока "Наши контакты" */}
                </li>
            </div>
            <Footer />
        </div>
    );
};

export default Faq;
