import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard'; // Предположим, что у вас есть компонент ProductCard

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
      const data = await response.json();
      setProducts((prevProducts) => [...prevProducts, ...data]); // Добавляем новые товары к существующим
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка загрузки товаров', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // Выполняем при монтировании компонента

  // Обработчик прокрутки страницы
  const handleScroll = () => {
    if (window.innerHeight + document.documentElement.scrollTop === document.documentElement.offsetHeight) {
      // Когда прокрутка достигает нижней части страницы
      fetchProducts();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []); // Подписываемся на событие прокрутки при монтировании и отписываемся при размонтировании

  return (
    <div>
      <div className="product-grid">
        {products.map((product, index) => (
          <ProductCard key={index} product={product} />
        ))}
      </div>
      {loading && <p>Loading...</p>}
    </div>
  );
};

export default ProductsPage;
