// ProductPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { silhouette, color, vendorCode } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://78.40.217.250:8081/api/sneakers/${silhouette}/${color}/${vendorCode}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
  
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Ошибка', error);
      }
    };
  
    fetchData();
  }, [silhouette, color, vendorCode]);
  
  

  return (
    <div>
      <h1>{`${silhouette} ${color} ${vendorCode}`}</h1>
      {product ? (
        <div>
          <p>{product.description}</p>
          {/* Другие свойства продукта */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductPage;
