import React, { useState } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
  const availableSizes = sizes.map(size => parseFloat(size));

  // Состояние для хранения выбранного размера
  const [selectedSize, setSelectedSize] = useState(null);

  // Обработчик изменения выбранного размера
  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  // Отображение текста для недоступных размеров
  const renderOptionLabel = (option) => (
    availableSizes.includes(parseFloat(option.value)) ? `${option.value} US` : `(${option.value} US) Нет в наличии`
  );

  return (
    <div className={classes.productSizeDropdown}>
      <label htmlFor="sizeDropdown">Выберите размер:</label>
      <Dropdown
        id="sizeDropdown"
        options={Array.from({ length: 22 }, (_, index) => ({ value: (index * 0.5 + 4).toString() }))}
        values={selectedSize ? [{ value: selectedSize.toString() }] : []}
        onChange={handleChange}
        placeholder="Выберите размер"
        labelField="value"
        valueField="value"
        dropdownHandle={false}
        searchable={false}
        clearable={false}
        keepSelectedInList={false}
        closeOnSelect={true}
        contentRenderer={({ props, state }) => (
          <div {...props} style={{ ...props.style, zIndex: 9999 }}>
            {state.values.length > 0 ? renderOptionLabel(state.values[0]) : props.placeholder}
          </div>
        )}
        optionRenderer={({ props, state, methods }) => (
          <div {...props} onClick={() => methods.addItem(props.option)}>
            {renderOptionLabel(props.option)}
          </div>
        )}
      />
      {!availableSizes.some(size => size % 1 !== 0) && (
        <p className={classes.sizeNotAvailable}>
          К сожалению, вашего размера нет в наличии.
        </p>
      )}
      {availableSizes.every(size => size % 1 !== 0) && (
        <p className={classes.sizeNotAvailable}>
          К сожалению, вашего размера нет в наличии.{' '}
          <a href="https://t.me/NiceDunksManager" target="_blank" rel="noopener noreferrer">
            Свяжитесь с нашим менеджером
          </a>
        </p>
      )}
    </div>
  );
};

export default MyDropdownMenu;
