// ProductsPage.jsx

import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [priceFilter, setPriceFilter] = useState(false);
  const [noveltyFilter, setNoveltyFilter] = useState(false);
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [selectedSizeOptions, setSelectedSizeOptions] = useState([]);
  const navigate = useNavigate();

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
      const data = await response.json();
      const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
      setProducts((prevProducts) => [...prevProducts, ...newProducts]);
      setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка при загрузке товаров', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleScroll = () => {
    const scrollThreshold = 100;
    const isCloseToBottom =
      window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - scrollThreshold;

    if (isCloseToBottom && !loading) {
      fetchProducts();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading]);

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const formatBrand = (brand) => {
    const words = brand.split(/\s*,\s*/);
    if (words.length > 1) {
      return words.join(' X ');
    }
    return brand;
  };

  const handleSizeFilterClick = () => {
    setShowSizeOptions(!showSizeOptions);
  };

  const handleSizeOptionClick = (size) => {
    const updatedSizeOptions = selectedSizeOptions.includes(size)
      ? selectedSizeOptions.filter((selectedSize) => selectedSize !== size)
      : [...selectedSizeOptions, size];

    setSelectedSizeOptions(updatedSizeOptions);
  };

  const applyFilters = () => {
    let filteredProducts = [...products];

    // Фильтруем по цене
    if (priceFilter) {
      filteredProducts = filteredProducts.sort((a, b) => a.price - b.price);
    }

    // Фильтруем по новизне
    if (noveltyFilter) {
      filteredProducts = filteredProducts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    }

    // Фильтруем по размеру
    if (selectedSizeOptions.length > 0) {
      filteredProducts = filteredProducts.filter((product) =>
        product.sizes.some((size) => selectedSizeOptions.includes(size))
      );
    }

    setProducts(filteredProducts);
  };

  useEffect(() => {
    applyFilters();
  }, [priceFilter, noveltyFilter, selectedSizeOptions]);

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li onClick={() => setPriceFilter(!priceFilter)} style={{ color: priceFilter ? '#AC8BD5' : '' }}>
              Цена
            </li>
            <li onClick={() => setNoveltyFilter(!noveltyFilter)} style={{ color: noveltyFilter ? '#AC8BD5' : '' }}>
              Новизне
            </li>
            <li onClick={handleSizeFilterClick} style={{ color: selectedSizeOptions.length > 0 ? '#AC8BD5' : '' }}>
              Размеры
            </li>
            <li>Бренд</li>
          </ul>
        </div>

        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>
                {formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}
              </h2>
              <h2>{product.price}₽</h2>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>

      {showSizeOptions && (
        <div className={classes.SizeOptions}>
          <h2>Выберите размеры для сортировки</h2>
          <div>
            {[...new Set(products.flatMap((product) => product.sizes))].map((size) => (
              <button
                key={size}
                className={selectedSizeOptions.includes(size) ? classes.SelectedSize : ''}
                onClick={() => handleSizeOptionClick(size)}
              >
                {size}
              </button>
            ))}
          </div>
        </div>
      )}

      {loading && <p>Загрузка...</p>}
    </div>
  );
};

export default ProductsPage;
