import React, { useState, useEffect } from 'react';

const ProductPage = ({ match }) => {
  const { silhouette, color, vendorCode } = match.params;
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Получаем все данные
        const response = await fetch('http://78.40.217.250:8081/api/sneakers');
        const data = await response.json();

        // Ищем соответствующий продукт локально
        const foundProduct = data.find(p =>
          p.silhouette === silhouette &&
          p.color === color &&
          p.vendorCode === vendorCode
        );

        if (foundProduct) {
          setProduct(foundProduct);
        } else {
          setProduct(null);
          console.error('Продукт не найден');
        }
      } catch (error) {
        console.error('Ошибка', error);
      }
    };

    fetchData();
  }, [silhouette, color, vendorCode]);

  return (
    <div>
      {product ? (
        <div>
          <h2>{product.brand}</h2>
          <p>{product.description}</p>
          {/* Дополнительная информация о продукте */}
        </div>
      ) : (
        <p>Продукт не найден</p>
      )}
    </div>
  );
};

export default ProductPage;
