import React from 'react';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
const availableSizes = sizes.map(size => parseFloat(size));

  const handleSizeClick = size => {
    if (!availableSizes.includes(size)) {
      window.location.href = 'https://t.me/NiceDunksManager';
    } else {
      // Добавьте свою логику обработки выбранного размера здесь
      console.log(`Выбран размер ${size}`);
    }
  };

  return (
    <div className={classes.productSizeDropdown}>
      <label htmlFor="sizeDropdown">Выберите размер:</label>
      <select id="sizeDropdown" onChange={e => handleSizeClick(parseFloat(e.target.value))}>
        <option value="" disabled hidden>
          Выберите размер
        </option>
        {Array.from({ length: 22 }, (_, index) => index * 0.5 + 4).map(size => (
          <option key={size} value={size} disabled={!availableSizes.includes(size)}>
            {availableSizes.includes(size) ? `US ${size}` : `К сожалению, размера US ${size} нет в наличии. Свяжитесь с нашим менеджером`}
          </option>
        ))}
      </select>
    </div>
  );
};



export default MyDropdownMenu;
