import React, { useState } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';
import './MyDropdownMenu.css';

const MyDropdownMenu = ({ sizes, category }) => {
  const availableSizes = sizes.map((size) => {
    if (typeof size === 'number') {
      return size;
    } else if (size === 'ONE SIZE') {
      return size;
    } else {
      return parseFloat(size);
    }
  });
  const [selectedSize, setSelectedSize] = useState(null);

  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  const renderOptionLabel = (option) =>
    availableSizes.includes(parseFloat(option.value))
      ? `${option.value} US`
      : `${option.value} US Нет в наличии`;

  // Проверка категории "accessories"
  const defaultSizes = category === 'accessories' ? ['ONESIZE'] : [];

  const options = Array.from({ length: 21 }, (_, index) => {
    const sizeValue = (index * 0.5 + 4).toString();
    const isAvailable = availableSizes.includes(parseFloat(sizeValue)) || category === 'accessories';
    return { value: sizeValue, disabled: !isAvailable, label: renderOptionLabel({ value: sizeValue }) };
  });

  return (
    <div className='DropDown'>
      <div className={classes.dropdownContainer}>
        <Dropdown
          id="sizeDropdown"
          options={options}
          values={selectedSize ? [{ value: selectedSize.toString() }] : defaultSizes} // Используем defaultSizes
          onChange={handleChange}
          placeholder="Выберите размер"
          labelfield="label"
          valuefield="value"
          dropdownhandle={true}
          searchable={false}
          clearable={false}
          keepselectedinlist={false}
          closeonselect={true}
          contentrenderer={({ props, state }) => (
            <div {...props} style={{ ...props.style, zIndex: 9999 }}>
              {state.values.length > 0 ? renderOptionLabel(state.values[0]) : props.placeholder}
            </div>
          )}
          optionrenderer={({ props, state, methods }) => (
            <div
              {...props}
              onClick={() => !props.option.disabled && methods.addItem(props.option)}
              style={{ cursor: props.option.disabled ? 'not-allowed' : 'pointer', color: props.option.disabled ? '#999' : '#333' }}
            >
              {props.option.label}
            </div>
          )}
        />
        <button className={classes.addToCartButton}>Добавить в корзину</button>
      </div>
      <div className={classes.SizeNotAvailable}>
        <div className={classes.ContactUs}>
          Вашего размера не оказалось в наличии?<br /> Свяжитесь с нашим <a href="https://t.me/NiceDunksManager">менеджером</a> и он обязательно поможет вам в этом вопросе!
        </div>
      </div>
    </div>
  );
};

export default MyDropdownMenu;
