import React from 'react';
import classes from './Faq.module.css';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import Footer from '../../Footer/Footer';
import FAQBlock from './FAQBlock'; // Импортируем новый компонент FAQBlock
import questionIcon from '../../UI/icons/question-svgrepo-com.svg';
import shippingIcon from '../../UI/icons/shipping-fast-solid-svgrepo-com.svg';
import contactsIcon from '../../UI/icons/contact-us-svgrepo-com.svg';

const Faq = () => {
  return (
    <div>
      <HeaderNavigation />
      <div className={classes.faq__wrapper}>
        {/* Используем компонент FAQBlock для каждого блока FAQ */}
        <FAQBlock
          icon={questionIcon}
          title="Частые вопросы по заказам"
          description="Предзаказы и их сроки, гарантии, отсутствие товара на сайте и другие вопросы."
        />
        <FAQBlock
          icon={shippingIcon}
          title="Доставка и оплата"
          description="Варианты и сроки доставки, сроки отправки, способы оплаты, доставка в другие страны и другие вопросы."
        />
        <FAQBlock
          icon={contactsIcon}
          title="Наши контакты"
          description="Ссылки на наши социальные сети, перечень товаров под заказ и многое другое."
        />
      </div>
      <Footer />
    </div>
  );
};

export default Faq;
