// ProductPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { silhouette, color, vendorCode } = useParams();
  const [allProducts, setAllProducts] = useState([]);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Загрузка всех данных
    fetch('http://78.40.217.250:8081/api/sneakers')
      .then(response => response.json())
      .then(data => setAllProducts(data))
      .catch(error => console.error('Ошибка', error));
  }, []);

  useEffect(() => {
    // Нахождение нужного продукта
    if (allProducts.length > 0) {
      const foundProduct = allProducts.find(
        p => p.silhouette === silhouette && p.color === color && p.vendorCode === vendorCode
      );
      setProduct(foundProduct);
    }
  }, [silhouette, color, vendorCode, allProducts]);

  return (
    <div>
      <h1>{`${silhouette} ${color} ${vendorCode}`}</h1>
      {product ? (
        <div>
          <p>{product.description}</p>
          {/* Другие свойства продукта */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductPage;
