import React, { useState } from 'react';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
  const availableSizes = sizes.map(size => parseFloat(size));
  const [selectedSize, setSelectedSize] = useState(null);

  const handleChange = (event) => {
    const newSize = parseFloat(event.target.value);
    setSelectedSize(newSize);
  };

  return (
    <div className={classes.productSizeDropdown}>
      <label htmlFor="sizeDropdown" className={classes.dropbtn}>
        {selectedSize ? `${selectedSize} US` : 'Выберите размер'}
      </label>
      <select id="sizeDropdown" value={selectedSize || ''} onChange={handleChange}>
        <option value="" disabled hidden>
          Выберите размер
        </option>
        {Array.from({ length: 22 }, (_, index) => index * 0.5 + 4).map(size => (
          <option key={size} value={size} disabled={!availableSizes.includes(size)}>
            {availableSizes.includes(size) ? `${size} US` : `(${size} US) Нет в наличии`}
          </option>
        ))}
      </select>
      {!availableSizes.includes(selectedSize) && (
        <p className={classes.sizeNotAvailable}>
          {selectedSize
            ? `К сожалению, размер ${selectedSize} US отсутствует в наличии.`
            : 'Выберите доступный размер из списка.'}{' '}
          <button onClick={() => window.location.href = 'https://t.me/NiceDunksManager'}>
            Свяжитесь с нашим менеджером
          </button>
        </p>
      )}
    </div>
  );
};

export default MyDropdownMenu;
