import React, { useState, useEffect } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';
import './MyDropdownMenu.css';

const MyDropdownMenu = ({ sizes, category }) => {
  const [availableSizes, setAvailableSizes] = useState([]);
  const [selectedSize, setSelectedSize] = useState(null);

  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  const renderOptionLabel = (option) =>
    availableSizes.includes(option.value)
      ? `${option.value} US`
      : `${option.value} US Нет в наличии`;

  useEffect(() => {
    let updatedAvailableSizes;

    if (category === 'clothes') {
      // Получите доступные размеры для одежды
      // Предполагается, что размеры для одежды фиксированные (XS, S, M, L, XL)
      size = ['XS', 'S', 'M', 'L', 'XL'].filter((size) => sizes.includes(size));
    } else if (category === 'accessories') {
      // Размер ONE SIZE для аксессуаров
      updatedAvailableSizes = ['ONESIZE'];
    } else {
      // Логика по умолчанию для других категорий
      updatedAvailableSizes = sizes;
    }

    setAvailableSizes(updatedAvailableSizes);
  }, [category, sizes]);

  const options = sizes.map((size) => ({
    value: size,
    disabled: !availableSizes.includes(size),
    label: renderOptionLabel({ value: size }),
  }));  

  return (
    <div className='DropDown'>
      <div className={classes.dropdownContainer}>
        <Dropdown
          id="sizeDropdown"
          options={options}
          values={selectedSize ? [{ value: selectedSize.toString() }] : []}
          onChange={handleChange}
          placeholder="Выберите размер"
          labelfield="label"
          valuefield="value"
          dropdownhandle={true}
          searchable={false}
          clearable={false}
          keepselectedinlist={false}
          closeonselect={true}
          contentrenderer={({ props, state }) => (
            <div {...props} style={{ ...props.style, zIndex: 9999 }}>
              {state.values.length > 0 ? renderOptionLabel(state.values[0]) : props.placeholder}
            </div>
          )}
          optionrenderer={({ props, state, methods }) => (
            <div
              {...props}
              onClick={() => !props.option.disabled && methods.addItem(props.option)}
              style={{
                cursor: props.option.disabled ? 'not-allowed' : 'pointer',
                color: props.option.disabled ? '#999' : '#333',
              }}
            >
              {props.option.label}
            </div>
          )}
        />
        <button className={classes.addToCartButton}>Добавить в корзину</button>
      </div>
      <div className={classes.SizeNotAvailable}>
        <div className={classes.ContactUs}>
          Вашего размера не оказалось в наличии?<br /> Свяжитесь с нашим{' '}
          <a href="https://t.me/NiceDunksManager">менеджером</a> и он обязательно поможет вам в этом вопросе!
        </div>
      </div>
    </div>
  );
};

export default MyDropdownMenu;
