import React from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { id } = useParams();

  // Разбиваем параметр на silhouette, color и vendorCode
  const [silhouette, color, vendorCode] = id.split('-');

  // Здесь вы можете использовать silhouette, color и vendorCode для получения данных о товаре
  // и отобразить их на странице

  return (
    <div>
      <h2>Product Details for ID: {id}</h2>
      <p>Silhouette: {silhouette}</p>
      <p>Color: {color}</p>
      <p>Vendor Code: {vendorCode}</p>
      {/* Добавьте код для отображения деталей товара */}
    </div>
  );
};

export default ProductPage;
