import React, { useState } from 'react';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
  const availableSizes = sizes.map(size => parseFloat(size));
  const [menuVisible, setMenuVisible] = useState(false);

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  return (
    <div className={`${classes.productSizeDropdown} ${menuVisible ? classes.show : ''}`}>
      <button onClick={toggleMenu} className={classes.dropbtn}>
        {availableSizes.length === 0 ? 'Нет в наличии' : 'Выберите размер'}
      </button>
      <div id="myDropdown" className={classes.dropdownContent}>
        <p className={classes.sizeNotAvailable}>
          {availableSizes.length === 0
            ? 'К сожалению, размеров нет в наличии.'
            : 'Выберите доступный размер из списка.'}
        </p>
        {availableSizes.map(size => (
          <a
            key={size}
            href="#"
            className={availableSizes.includes(size) ? '' : classes.notAvailable}
            onClick={() => {
              if (!availableSizes.includes(size)) {
                window.location.href = 'https://t.me/NiceDunksManager';
              }
            }}
          >
            {availableSizes.includes(size) ? `${size} US` : `(${size} US)`}
          </a>
        ))}
      </div>
    </div>
  );
};

export default MyDropdownMenu;
