import React from 'react';
import classes from './MyDropdownMenu.module.css';

const MyDropdownMenu = ({ sizes }) => {
  const availableSizes = sizes.map(size => parseFloat(size));

  return (
    <div className={classes.productSizeDropdown}>
      <label className={classes.dropdownLabel} htmlFor="sizeDropdown">
        Выберите размер:
      </label>
      <select id="sizeDropdown" className={classes.dropdownSelect}>
        <option value="" disabled hidden>
          {availableSizes.length === 0 ? 'Нет в наличии' : 'Выберите размер'}
        </option>
        {Array.from({ length: 22 }, (_, index) => index * 0.5 + 4).map(size => (
          <option
            key={size}
            value={size}
            disabled={!availableSizes.includes(size)}
          >
            {availableSizes.includes(size) ? `${size} US` : `(${size} US) Нет в наличии`}
          </option>
        ))}
      </select>
      {!availableSizes.every(size => size % 1 !== 0) && (
        <p className={classes.sizeNotAvailable}>
          К сожалению, вашего размера нет в наличии.
        </p>
      )}
      {availableSizes.every(size => size % 1 !== 0) && (
        <p className={classes.sizeNotAvailable}>
          К сожалению, вашего размера нет в наличии.{' '}
          <button onClick={() => window.location.href = 'https://t.me/NiceDunksManager'}>
            Свяжитесь с нашим менеджером
          </button>
        </p>
      )}
    </div>
  );
};

export default MyDropdownMenu;
