import React, { useState } from 'react';
import classes from './Faq.module.css';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import Footer from '../../Footer/Footer';
import FAQBlock from './FAQBlock/FAQBlock';
import questionIcon from '../../UI/icons/question-svgrepo-com.svg';
import shippingIcon from '../../UI/icons/shipping-fast-solid-svgrepo-com.svg';
import contactsIcon from '../../UI/icons/contact-us-svgrepo-com.svg';

const Faq = () => {
    const [shippingDetailsOpen, setShippingDetailsOpen] = useState(false);
    
    const toggleShippingDetails = () => {
        setShippingDetailsOpen(!shippingDetailsOpen);
    }

    return (
        <div>
            <HeaderNavigation />
            <div className={classes.faq__wrapper}>
                <FAQBlock
                    icon={questionIcon}
                    title="Частые вопросы по заказам"
                    description="Предзаказы и их сроки, гарантии, отсутствие товара на сайте и другие вопросы."
                />
                <FAQBlock
                    icon={shippingIcon}
                    title="Доставка и оплата"
                    onClick={toggleShippingDetails}
                    description="Варианты и сроки доставки, сроки отправки, способы оплаты, доставка в другие страны и другие вопросы."
                />
                <li className={`${classes.shippingDetails} ${shippingDetailsOpen ? classes.visible : ''}`}>
                    <div>Самовывоз в городе Москва в районе метро Ботанический Сад</div>
                    <div>Курьерская доставка по Москве осуществляется сервисами: Яндекс Go и Достависта</div>
                    <div>Доставка по России и СНГ осуществляется почтовыми службами: Сдэк, Почта РФ и Boxberry</div>
                    <div>Стоимость доставки рассчитывается отдельно</div>
                    <div>Доставка по миру осуществляется курьерской службой EMS Почта России</div>
                    <div>Стоимость доставки рассчитывается отдельно</div>
                </li>
                <FAQBlock
                    icon={contactsIcon}
                    title="Наши контакты"
                    description="Ссылки на наши социальные сети, перечень товаров под заказ и многое другое."
                />
            </div>
            <Footer />
        </div>
    );
};

export default Faq;
