import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import HeaderNavigation from '../../UI/HeaderNavigation/HeaderNavigation';
import classes from './ProductPage.module.css';

const ProductPage = () => {
    const { vendorCode } = useParams();
    const [product, setProduct] = useState(null);
    const [brand, setBrand] = useState(null);
    const [silhouette, setSilhouette] = useState(null);
    const [color, setColor] = useState(null);
    const [price, setPrice] = useState(null);
    const [description, setDescription] = useState(null);
  
    useEffect(() => {
      fetch(`http://78.40.217.250:8081/api/sneakersById?id=${vendorCode.toUpperCase()}`)
        .then(response => response.json())
        .then(data => {
          setProduct(data);
          setBrand(data.brand);
          setSilhouette(data.silhouette);
          setColor(data.color);
          setPrice(data.price);
          setDescription(data.description);
        })
        .catch(error => console.error('Ошибка', error));
    }, [vendorCode]);
  
    const formatBrand = (brand) => {
        // Если бренд состоит из двух слов, разделим их знаком "X"
        const words = brand.split(/\s*,\s*/);
        if (words.length > 1) {
          return words.join(' X ');
        }
        return brand;
      };


  return (
    <div>
      <HeaderNavigation />
      <div className={classes.productPage__wrapper}>
        {product && product.images ? (
          <div className={classes.productPage__images}>
            {product.images.map((image, index) => (
              <img key={index} src={image} alt={`Product Image ${index}`} />
            ))}
          </div>
        ) : (
          <p>Loading...</p>
        )}
        <div>
            <h1 className={classes.ProductPage__brand}>
                {product && formatBrand(product.brand)}
            </h1>
            <h2 className={classes.ProductPage__colorway}> 
            {silhouette + ' ' + color}
            </h2>
            <h3 className={classes.ProductPage__price}>
                {price + ' ₽'}
            </h3>
            <div className={classes.description__block}>
                <div className={classes.description__title}>ОПИСАНИЕ</div>
                <div className={classes.description__text}>{description}</div>
            </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
