// ProductPage.jsx
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { silhouette, color, vendorCode } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Здесь делаете запрос к API, чтобы получить информацию о конкретном продукте
    fetch(`http://78.40.217.250:8081/api/sneakers/${silhouette}/${color}/${vendorCode}`)
      .then((response) => response.json())
      .then((data) => setProduct(data))
      .catch((error) => console.error('Ошибка', error));
  }, [silhouette, color, vendorCode]);

  if (!product) {
    // Можете добавить лоадер или другой контент, который будет отображаться во время загрузки данных
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{product.brand + " " + product.silhouette + " " + product.color}</h2>
      <img src={product.images[0]} alt={product.brand} />

      {/* Добавьте другие компоненты для отображения описания, размеров и цены */}
      <p>Description: {product.description}</p>
      <p>Sizes: {product.sizes.join(', ')}</p>
      <p>Price: {product.price}</p>
    </div>
  );
};

export default ProductPage;
