import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [selectedSizes, setSelectedSizes] = useState([]);
  const [showSizeModal, setShowSizeModal] = useState(false);
  const [priceFilter, setPriceFilter] = useState(null);
  const [noveltyFilter, setNoveltyFilter] = useState(null);
  const navigate = useNavigate();

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
      const data = await response.json();
      const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
      setProducts((prevProducts) => [...prevProducts, ...newProducts]);
      setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка при загрузке товаров', error);
    } finally {
      setLoading(false);
    }
  };

  const handleScroll = () => {
    const scrollThreshold = 100;
    const isCloseToBottom =
      window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - scrollThreshold;

    if (isCloseToBottom && !loading) {
      fetchProducts();
    }
  };

  const applyFilters = () => {
    let filteredProducts = [...products];

    if (priceFilter) {
      filteredProducts = filteredProducts.sort((a, b) =>
        priceFilter === 'asc' ? a.price - b.price : b.price - a.price
      );
    }

    if (noveltyFilter) {
      filteredProducts = filteredProducts.sort((a, b) =>
        noveltyFilter === 'asc' ? new Date(a.createdAt) - new Date(b.createdAt) : new Date(b.createdAt) - new Date(a.createdAt)
      );
    }

    // Фильтруем по размеру
    if (selectedSizes.length > 0) {
      filteredProducts = filteredProducts.filter((product) =>
        product.sizes.some((size) => selectedSizes.includes(size))
      );
    }

    setProducts(filteredProducts);
  };

  useEffect(() => {
    applyFilters();
  }, [priceFilter, noveltyFilter, selectedSizes]);

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading]);

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const formatBrand = (brand) => {
    const words = brand.split(/\s*,\s*/);
    if (words.length > 1) {
      return words.join(' X ');
    }
    return brand;
  };

  const togglePriceFilter = () => {
    setPriceFilter((prevFilter) => (prevFilter === 'asc' ? 'desc' : 'asc'));
  };

  const toggleNoveltyFilter = () => {
    setNoveltyFilter((prevFilter) => (prevFilter === 'asc' ? 'desc' : 'asc'));
  };

  const handleSizeClick = (size) => {
    const updatedSizes = selectedSizes.includes(size)
      ? selectedSizes.filter((selectedSize) => selectedSize !== size)
      : [...selectedSizes, size];

    setSelectedSizes(updatedSizes);
  };

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li onClick={togglePriceFilter} style={{ color: priceFilter ? '#AC8BD5' : '' }}>
              Цена {priceFilter && (priceFilter === 'asc' ? '↑' : '↓')}
            </li>
            <li onClick={toggleNoveltyFilter} style={{ color: noveltyFilter ? '#AC8BD5' : '' }}>
              Новизне {noveltyFilter && (noveltyFilter === 'asc' ? '↑' : '↓')}
            </li>
            <li onClick={() => setShowSizeModal(true)} style={{ color: selectedSizes.length > 0 ? '#AC8BD5' : '' }}>
              Размеры
            </li>
            <li>Бренд</li>
          </ul>
        </div>
        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>{formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}</h2>
              <h2>{product.price}₽</h2>
              <p>Sizes: {product.sizes.join(', ')}</p>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>
      {loading && <p>Загрузка...</p>}

      {/* Модальное окно выбора размеров */}
      {showSizeModal && (
        <div className={classes.ModalOverlay}>
          <div className={classes.SizeModal}>
            <h2>Выберите размеры</h2>
            <div>
              {[...new Set(products.flatMap((product) => product.sizes))].map((size) => (
                <button
                  key={size}
                  className={selectedSizes.includes(size) ? classes.SelectedSize : ''}
                  onClick={() => handleSizeClick(size)}
                >
                  {size}
                </button>
              ))}
            </div>
            <button onClick={() => setShowSizeModal(false)}>Применить</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
