// ProductPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { silhouette, color, vendorCode } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Здесь вы можете использовать fetch или другой метод для получения данных о товаре
    // Например, если вы хотите получить данные с вашего API:
    fetch(`http://78.40.217.250:8081/api/sneakers/${silhouette}/${color}/${vendorCode}`)
      .then(response => response.json())
      .then(data => setProduct(data))
      .catch(error => console.error('Ошибка', error));
  }, [silhouette, color, vendorCode]);

  return (
    <div>
      {product ? (
        <div>
          <h1>{`${product.brand} ${product.silhouette} ${product.color} ${product.vendorCode}`}</h1>
          <p>{product.description}</p>
          {/* Вывод других свойств продукта */}
          <p>Price: {product.price}</p>
          <p>Sizes: {product.sizes.join(', ')}</p>
          {/* Другие свойства продукта */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductPage;
