import React, { useState } from 'react'
import './MyBurger.css'

const MyBurger = () => {
    const [isActive, setActive] = useState();
    const [modal, setModal] = useState(false)


    const handleToggle = () => {
        setActive(!isActive)
    }

  return (
    <div className={isActive ? 'burger open' : 'burger'} onClick={handleToggle && setModal(true)}>
        <span></span>
    </div>
  )
}

export default MyBurger;
