import React, { useEffect, useState } from 'react';
import classes from './HeaderNavigation.module.css';
import searchIcon from '../icons/search.svg';
import cartIcon from '../icons/headercart.svg';
import Cart from '../../Cart/Cart';
import { useCart } from '../../Cart/CartContext';
import { useNavigate } from 'react-router-dom';

const HeaderNavigation = ({ cartItems }) => {
  const { cart } = useCart();
  const [headerWords] = useState([
    'НОВЫЕ ПОСТУПЛЕНИЯ',
    'ОДЕЖДА',
    'АКСЕССУАРЫ',
    'НОВЫЕ КРОССОВКИ',
    'БРЕНДЫ',
    'Б/У КРОССОВКИ',
    'ПРЕДЗАКАЗ',
    'НА ГЛАВНУЮ',
  ]);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [brandsModalOpen, setBrandsModalOpen] = useState(false);
  const [brands, setBrands] = useState([]); 
  const navigate = useNavigate();
  

  const wordMapping = {
    'НОВЫЕ ПОСТУПЛЕНИЯ': '',
    'ОДЕЖДА': 'clothes',
    'АКСЕССУАРЫ': 'accessories',
    'НОВЫЕ КРОССОВКИ': 'newSneakers',
    'БРЕНДЫ': '',
    'Б/У КРОССОВКИ': 'used',
    'ПРЕДЗАКАЗ': 'preorder',
    'НА ГЛАВНУЮ': '/',
  };

  const navigateTo = (word) => {
    if (word === 'НА ГЛАВНУЮ') {
      window.location.href = '/';
    } else if (word === 'cart' && cart.length > 0) {
      setIsCartOpen(true);
    } else if (word === 'cart' && cart.length === 0) {
      return;
    } else if (word === 'БРЕНДЫ') {
      toggleBrandsModal(); // Открываем модальное окно с брендами
    } else {
      const englishWord = wordMapping[word] || '';
      window.location.href = `/all%20products?category=${englishWord}`;
    }
  };

  // Функция для загрузки списка брендов из API
  const fetchBrands = async () => {
    try {
      const response = await fetch('https://nicedunks.ru/api/brands');
      const data = await response.json();
      setBrands(data);
    } catch (error) {
      console.error('Ошибка при загрузке брендов:', error);
    }
  };

  useEffect(() => {
    fetchBrands();
  }, []);

  const handleSearchIconClick = () => {
    setIsSearchModalOpen(true);
  };

  const handleSearchInputChange = async (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    if (query.trim() !== '') {
      try {
        const response = await fetch(`https://nicedunks.ru/api/search?query=${query}`);
        const data = await response.json();
        setSearchResults(data);
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    } else {
      setSearchResults([]);
    }
  };

  const closeCart = () => {
    setIsCartOpen(false);
  };

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const formatBrand = (brand) => {
    const words = brand.split(/\s*,\s*/);
    if (words.length > 1) {
      return words.join(' X ');
    }
    return brand;
  };

  const handleModalClose = () => {
    setSearchQuery('');
    setSearchResults([]);
    setIsSearchModalOpen(false);
  };

  const handleBrandOptionClick = (brand) => {
    const link = `/all products/?brand=${brand}`
    navigate(link)
  }

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === 'Escape') {
        handleModalClose();
      }
    };

    if (isSearchModalOpen) {
      window.addEventListener('keydown', handleKeyPress);
    }

    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [isSearchModalOpen]);

  const toggleBrandsModal = () => {
    setBrandsModalOpen(!brandsModalOpen);
  };

  return (
    <div className={classes.HeaderNavigation__wrapper}>
      <div className={classes.SearchWrapper} onClick={handleSearchIconClick} style={{ cursor: 'pointer'}}>
        <img src={searchIcon} alt="поиск" style={{ marginTop: 39, cursor: 'pointer', marginLeft: 0, marginRight: 0, maxWidth: 100 }} />
      </div>
      {headerWords.map((word, index) => (
        <div
          className={`${classes.HeaderNavigation} ${word === 'ПРЕДЗАКАЗ' ? classes.preorderButton : ''}`}
          key={index}
          onClick={() => navigateTo(word)}
        >
          {word}
        </div>
      ))}
      <div className={classes.CartWrapper} onClick={() => navigateTo('cart')} style={{cursor: 'pointer'}}>
        <img src={cartIcon} alt="корзина" style={{ marginTop: 38, cursor: 'pointer', marginLeft: 0, marginRight: 0, maxWidth: 100}} />
        {cart.length !== 0 ? " " + cart.length : " "}
      </div>
      {isCartOpen && <Cart cartItems={cartItems} onClose={closeCart} />}
      {isSearchModalOpen && (
        <div className={classes.SearchModalContainer}>
          <div сlassName={classes.ModalContent}>
            <div className={classes.ModalSearch}>
              <img src={searchIcon} alt="поиск" style={{ marginTop: 38, cursor: 'pointer', marginLeft: 10, marginRight: 0 }} />
                <input
                  type="text"
                  value={searchQuery}
                  onChange={handleSearchInputChange}
                  placeholder="Искать в каталоге"
                />
                <div className={classes.CloseButton} onClick={handleModalClose}>Х</div>
            </div>
            {searchResults && searchResults.length > 0 ? (
                  <div className={classes.Products__Wrapper}>
                      {searchResults.map((product, index) => (
                          <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
                              <h2>
                                  {formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}
                              </h2>
                              <h2>{product.price}₽</h2>
                              <img src={product.images[0]} alt={product.brand} />
                          </div>
                      ))}
                  </div>
              ) : (
                  <p> </p>
              )}
            </div>
        </div>
      )}
      {brandsModalOpen && (
        <div className={classes.BrandsModalContainer}>
          <h2>СПИСОК БРЕНДОВ:</h2>
          <div className={classes.BrandsModalContent}>
              {brands.map((brand, index) => (
                <p key={index} onClick={() => handleBrandOptionClick(brand)}>{brand}</p>
              ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default HeaderNavigation;
