import React, { useState, useEffect } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';
import './MyDropdownMenu.css';

const generateSizeOptions = (availableSizesArray, category) => {
  const allSizes = [];
  for (let size = 4; size <= 14; size += 0.5) {
    allSizes.push(size.toFixed(1));
  }

  return allSizes.map(size => {
    const isAvailable = availableSizesArray.includes(parseFloat(size));
    return { value: size.toString(), disabled: !isAvailable, label: isAvailable ? size.toString() : `${size} Нет в наличии` };
  });
};

const MyDropdownMenu = ({ sizes, availableSizes, category }) => {
  const validSizes = sizes.map(size => (size === 'ONESIZE' ? size : parseFloat(size)));
  const availableSizesArray = validSizes.filter(size => typeof size === 'number');
  const [selectedSize, setSelectedSize] = useState(null);

  useEffect(() => {
    // Reset selected size when sizes change
    setSelectedSize(null);
  }, [sizes]);

  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  const renderOptionLabel = (option) => {
    const hasAvailability = availableSizesArray.includes(parseFloat(option.value)) || category === 'accessories';
    return hasAvailability ? option.value : `${option.value} Нет в наличии`;
  };

  const defaultSizes = category === 'accessories' ? ['ONESIZE'] : [];

  const options = generateSizeOptions(availableSizesArray, category);

  return (
    <div className='DropDown'>
      <div className={classes.dropdownContainer}>
        <Dropdown
          id="sizeDropdown"
          options={options}
          values={selectedSize ? [{ value: selectedSize.toString() }] : defaultSizes}
          onChange={handleChange}
          placeholder="Выберите размер"
          labelfield="label"
          valuefield="value"
          dropdownhandle={true}
          searchable={false}
          clearable={false}
          keepselectedinlist={false}
          closeonselect={true}
          contentrenderer={({ props, state }) => (
            <div {...props} style={{ ...props.style, zIndex: 9999 }}>
              {state.values.length > 0 ? renderOptionLabel(state.values[0]) : props.placeholder}
            </div>
          )}
          optionrenderer={({ props, state, methods }) => (
            <div
              {...props}
              onClick={() => !props.option.disabled && methods.addItem(props.option)}
              style={{ cursor: props.option.disabled ? 'not-allowed' : 'pointer', color: props.option.disabled ? '#999' : '#333' }}
            >
              {props.option.label}
            </div>
          )}
        />
        <button className={classes.addToCartButton}>Добавить в корзину</button>
      </div>
      <div className={classes.SizeNotAvailable}>
        <div className={classes.ContactUs}>
          Вашего размера не оказалось в наличии?<br /> Свяжитесь с нашим <a href="https://t.me/NiceDunksManager">менеджером</a> и он обязательно поможет вам в этом вопросе!
        </div>
      </div>
    </div>
  );
};

export default MyDropdownMenu;
