import React, { useState, useEffect } from 'react';
import classes from './ProductsPage.module.css';
import { useNavigate } from 'react-router-dom';

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [loadedProductIds, setLoadedProductIds] = useState(new Set());
  const [sortOrder, setSortOrder] = useState('desc'); // 'asc' или 'desc'
  const [sortBy, setSortBy] = useState('createdAt');
  const [showSizeOptions, setShowSizeOptions] = useState(false);
  const [availableSizes, setAvailableSizes] = useState([]);
  const [selectedSizeOptions, setSelectedSizeOptions] = useState([]);
  const [availableBrands, setAvailableBrands] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [clickCount, setClickCount] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await fetch('http://78.40.217.250:8081/api/brands');
        const data = await response.json();
        setAvailableBrands(data);
      } catch (error) {
        console.error('Ошибка при загрузке брендов', error);
      }
    };

    fetchBrands();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://78.40.217.250:8081/api/sneakers?page=${page}`);
      const data = await response.json();
      const newProducts = data.filter((product) => !loadedProductIds.has(product.vendorCode));
      newProducts.reverse();
      setProducts((prevProducts) => [...prevProducts, ...newProducts]);
      setLoadedProductIds((prevIds) => new Set([...prevIds, ...newProducts.map((product) => product.vendorCode)]));
      setPage((prevPage) => prevPage + 1);
    } catch (error) {
      console.error('Ошибка при загрузке товаров', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleScroll = () => {
    const scrollThreshold = 100;
    const isCloseToBottom =
      window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - scrollThreshold;

    if (isCloseToBottom && !loading) {
      fetchProducts();
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading]);

  const redirectToProductPage = (vendorCode) => {
    const link = `/product/${vendorCode.toLowerCase()}`;
    navigate(link);
  };

  const formatBrand = (brand) => {
    const words = brand.split(/\s*,\s*/);
    return words.length > 1 ? words.join(' X ') : brand;
  };

  const handleSortClick = (sortType) => {
    if (sortType === sortBy) {
      setSortOrder((prevSortOrder) => (prevSortOrder === 'asc' ? 'desc' : 'asc'));
    } else {
      setSortBy(sortType);
      setSortOrder('asc');
    }
  };

  const handleSizeFilterClick = () => {
    setShowSizeOptions((prevShowSizeOptions) => !prevShowSizeOptions);
    setClickCount((prevCount) => prevCount + 1);
  };

  useEffect(() => {
    if (clickCount === 3) {
      window.location.reload();
    }
  }, [clickCount]);

  const handleSizeOptionClick = (size) => {
    setSelectedSizeOptions((prevOptions) =>
      prevOptions.includes(size) ? prevOptions.filter((option) => option !== size) : [...prevOptions, size]
    );
  };

  const handleBrandFilterClick = (brand) => {
    setSelectedBrands((prevBrands) =>
      prevBrands.includes(brand) ? prevBrands.filter((selectedBrand) => selectedBrand !== brand) : [...prevBrands, brand]
    );
  };

  const applyFilters = () => {
    let filteredProducts = [...products];

    // Фильтр по размерам
    if (selectedSizeOptions.length > 0) {
      filteredProducts = filteredProducts.filter((product) =>
        product.sizes.some((size) => selectedSizeOptions.includes(size))
      );
    }

    // Фильтр по брендам
    if (selectedBrands.length > 0) {
      filteredProducts = filteredProducts.filter((product) => selectedBrands.includes(product.brand));
    }

    // Сортировка
    if (sortBy === 'price' || sortBy === 'createdAt') {
      filteredProducts.sort((a, b) => {
        const valueA = sortBy === 'price' ? a.price : new Date(a.createdAt);
        const valueB = sortBy === 'price' ? b.price : new Date(b.createdAt);
        return sortOrder === 'asc' ? valueA - valueB : valueB - valueA;
      });
    }

    setProducts(filteredProducts);
  };

  useEffect(() => {
    applyFilters();
  }, [selectedSizeOptions, sortOrder, sortBy, selectedBrands]);

  useEffect(() => {
    const sizes = [...new Set(products.flatMap((product) => product.sizes))];
    sizes.sort((a, b) => parseFloat(a) - parseFloat(b));
    setAvailableSizes(sizes);
  }, [products]);

  return (
    <div>
      <div className={classes.ProductsPage__Wrapper}>
        <div className={classes.ProductsPage__Filters}>
          <p>Сортировать по:</p>
          <ul>
            <li onClick={handleSizeFilterClick} style={{ color: selectedSizeOptions.length > 0 ? '#AC8BD5' : '' }}>
              Размеры
              {showSizeOptions && (
                <div className={classes.SizeOptions}>
                  {availableSizes.map((size) => (
                    <button
                      key={size}
                      className={selectedSizeOptions.includes(size) ? classes.SelectedSize : ''}
                      onClick={() => handleSizeOptionClick(size)}
                    >
                      {size}
                    </button>
                  ))}
                </div>
              )}
            </li>
            <li onClick={() => handleSortClick('price')} style={{ color: sortBy === 'price' ? '#AC8BD5' : '' }}>
              Цена {sortBy === 'price' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
            <li onClick={() => handleSortClick('createdAt')} style={{ color: sortBy === 'createdAt' ? '#AC8BD5' : '' }}>
              Новизне {sortBy === 'createdAt' && (sortOrder === 'asc' ? '↑' : '↓')}
            </li>
            <li>
              Бренды
              {availableBrands.map((brand) => (
                <button
                  key={brand}
                  className={selectedBrands.includes(brand) ? classes.SelectedBrand : ''}
                  onClick={() => handleBrandFilterClick(brand)}
                >
                  {brand}
                </button>
              ))}
            </li>
          </ul>
        </div>

        <div className={classes.Products__Wrapper}>
          {products.map((product, index) => (
            <div key={index} className={classes.product__card} onClick={() => redirectToProductPage(product.vendorCode)}>
              <h2>
                {formatBrand(product.brand) + ' ' + product.silhouette + ' ' + product.color}
              </h2>
              <h2>{product.price}₽</h2>
              <img src={product.images[0]} alt={product.brand} />
            </div>
          ))}
        </div>
      </div>

      {loading && <p>Загрузка...</p>}
    </div>
  );
};

export default ProductsPage;
