import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Выполните запрос к API для получения информации о товаре по его id
    fetch(`http://78.40.217.250:8081/api/sneakers/${id}`)
      .then((response) => response.json())
      .then((data) => setProduct(data))
      .catch((error) => console.error('ошибка', error));
  }, [id]);

  if (!product) {
    return <div>Loading...</div>;
  }

  // Отобразите информацию о товаре
  return (
    <div>
      <h1>{product.brand + " " + product.silhouette + " " + product.color}</h1>
      <img src={product.images[0]} alt={product.brand} />
    </div>
  );
};

export default ProductPage;
