import React from 'react';
import classes from './ProductCard.module.css';

const ProductCard = ({ product }) => {
  const { brand, images, silhouette, color, vendorCode } = product;
  const link = `/${silhouette.toLowerCase()}-${color.toLowerCase()}-${vendorCode}`;

  return (
    <div className={classes.product__card}>
      <a href={link}>
        <img src={images[0]} alt={brand} />
        <h2>{brand + " " + silhouette + " " + color}</h2>
      </a>
    </div>
  );
};

export default ProductCard;
