import React, { useState, useEffect } from 'react';
import Dropdown from 'react-dropdown-select';
import classes from './MyDropdownMenu.module.css';
import './MyDropdownMenu.css';

const MyDropdownMenu = ({ sizes, category }) => {
  const availableSizes = sizes.map((size) => parseFloat(size));
  const [selectedSize, setSelectedSize] = useState(null);
  const [sizeOptions, setSizeOptions] = useState([]);

  useEffect(() => {
    const generateSizeOptions = () => {
      if (category === 'clothes') {
        // Если это категория одежды, создаем размеры XS, S, M, L, XL
        const clothesSizes = ['XS', 'S', 'M', 'L', 'XL'];
        return clothesSizes
          .filter((size) => availableSizes.includes(parseFloat(size)))
          .map((size) => ({
            value: size,
            label: `${size} US`,
          }));
      } else if (category === 'shoes') {
        // Если это категория обуви, используем размеры из базы
        return sizes.map((size) => ({
          value: size.toString(),
          label: `${size} US`,
        }));
      } else {
        // Для других категорий, включая аксессуары, используем "ONESIZE"
        return [{ value: 'ONESIZE', label: 'ONESIZE' }];
      }
    };

    setSizeOptions(generateSizeOptions());
  }, [sizes, category]);

  const handleChange = (values) => {
    if (values.length > 0) {
      setSelectedSize(values[0].value);
    } else {
      setSelectedSize(null);
    }
  };

  return (
    <div className='DropDown'>
      <div className={classes.dropdownContainer}>
        <Dropdown
          id="sizeDropdown"
          options={sizeOptions}
          values={selectedSize ? [{ value: selectedSize.toString() }] : []}
          onChange={handleChange}
          placeholder="Выберите размер"
          labelfield="label"
          valuefield="value"
          dropdownhandle={true}
          searchable={false}
          clearable={false}
          keepselectedinlist={false}
          closeonselect={true}
          contentrenderer={({ props, state }) => (
            <div {...props} style={{ ...props.style, zIndex: 9999 }}>
              {state.values.length > 0 ? state.values[0].label : props.placeholder}
            </div>
          )}
          optionrenderer={({ props, state, methods }) => (
            <div
              {...props}
              onClick={() => !props.option.disabled && methods.addItem(props.option)}
              style={{ cursor: props.option.disabled ? 'not-allowed' : 'pointer', color: props.option.disabled ? '#999' : '#333' }}
            >
              {props.option.label}
            </div>
          )}
        />
        <button className={classes.addToCartButton}>Добавить в корзину</button>
      </div>
      <div className={classes.SizeNotAvailable}>
        <div className={classes.ContactUs}>
          Вашего размера не оказалось в наличии?<br /> Свяжитесь с нашим <a href="https://t.me/NiceDunksManager">менеджером</a> и он обязательно поможет вам в этом вопросе!
        </div>
      </div>
    </div>
  );
};

export default MyDropdownMenu;
