import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import classes from './HeaderNavigation.module.css';
import search from '../icons/search.svg';
import cart from '../icons/headercart.svg';

const HeaderNavigation = () => {
  const location = useLocation();

  const [headerWords] = useState([
    'НОВЫЕ ПОСТУПЛЕНИЯ',
    'ОДЕЖДА',
    'АКСЕССУАРЫ',
    'НОВЫЕ КРОССОВКИ',
    'БРЕНДЫ',
    'Б/У КРОССОВКИ',
    'ПРЕДЗАКАЗ',
    'НА ГЛАВНУЮ',
  ]);

  useEffect(() => {
    // Handle location change logic here
    console.log('Location changed:', location.pathname);
  }, [location]);

  return (
    <div className={classes.HeaderNavigation__wrapper}>
      <img src={search} alt="поиск" style={{ marginTop: 36 }} />
      {headerWords.map((word, index) => (
        <Link
          to="/"
          className={`${classes.HeaderNavigation} ${
            word === 'ПРЕДЗАКАЗ' ? classes.preorderButton : ''
          }`}
          key={index}
        >
          {word}
        </Link>
      ))}
      <img src={cart} alt="корзина" style={{ marginTop: 36 }} />
    </div>
  );
};

export default HeaderNavigation;
