import React, { useState } from 'react';
import classes from '../Faq.module.css';

const FAQBlock = ({ icon, title, description, expandedContent }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpansion = () => {
    setExpanded(!expanded);
  };

  return (
    <div className={classes.faq__block} onClick={toggleExpansion}>
      <div className={classes.faq__blockWrapper}>
        <div className={classes.img__wrapper}>
          <img src={icon} alt={title} />
        </div>
        <h2 className={classes.block__title}>{title}</h2>
        <h3 className={classes.block__description}>{description}</h3>
        {expanded && expandedContent && (
          <div className={classes.expandedContent}>{expandedContent}</div>
        )}
      </div>
    </div>
  );
};

export default FAQBlock;
