const express = require('express');
const app = express();
const port = 8081;

// Ваши данные
const products = [
  // ... ваш массив данных ...
];

app.use(express.json());

// Получение всех кроссовок
app.get('/api/sneakers', (req, res) => {
  res.json(products);
});

// Получение данных для конкретной кроссовки
app.get('/api/sneakers/:brand/:silhouette/:color/:vendorCode', (req, res) => {
  const { brand, silhouette, color, vendorCode } = req.params;

  const product = products.find(p =>
    p.brand === brand &&
    p.silhouette === silhouette &&
    p.color === color &&
    p.vendorCode === vendorCode
  );

  if (product) {
    res.json(product);
  } else {
    res.status(404).send('Product not found');
  }
});

// Новый эндпоинт для поиска
app.get('/api/sneakers/search', (req, res) => {
  const { silhouette, color, vendorCode } = req.query;

  const searchResults = products.filter(product =>
    product.silhouette === silhouette &&
    product.color === color &&
    product.vendorCode === vendorCode
  );

  res.json(searchResults);
});

app.listen(port, () => {
  console.log(`Server is running on port ${port}`);
});
