import React, { useState, useEffect } from 'react';

const ProductPage = ({ match }) => {
  const { silhouette, color, vendorCode } = match.params;
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://78.40.217.250:8081/api/sneakers/${silhouette}/${color}/${vendorCode}`);
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Ошибка', error);
      }
    };

    fetchProduct();
  }, [silhouette, color, vendorCode]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const { brand, sizes, price, description, images } = product;

  return (
    <div>
      <h1>{brand + " " + silhouette + " " + color}</h1>
      <p>Description: {description}</p>
      <p>Sizes: {sizes.join(', ')}</p>
      <p>Price: {price}</p>
      <div>
        {images.map((imageUrl, index) => (
          <img key={index} src={imageUrl} alt={`Product ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
